        public async void ClaimToken()
        {
            // Reference to Thirdweb SDK
            var sdk = ThirdwebManager.Instance.SDK;
            string address = await sdk.Wallet.GetAddress();

            RacingUI.Instance.claimRaceFirstPlaceButtonText.text = "Claiming ...";
            RacingUI.Instance.claimRaceFirstPlaceButton.interactable = false;
            RacingUI.Instance.raceFirstPlacePanel.SetActive(false);

            // Show the backgroundFirstPlacePanel
            RacingUI.Instance.raceBackgroundFirstPlacePanel.SetActive(true);
            RacingUI.Instance.claimRaceBackgroundFirstPlaceButtonText.text = "Claiming ...";
            RacingUI.Instance.claimRaceBackgroundFirstPlaceButton.interactable = false;

            var contract = ThirdwebManager.Instance.SDK.GetContract("0xd497BC49Ab2758Aa48B42131822c7958e182a495");
            var result = await contract.ERC20.ClaimTo(address, 3.ToString());

            // claimFirstPlaceButtonText.text = "Claim successful";
            RacingUI.Instance.claimRaceFirstPlaceButtonText.text = "Claim successful";
            RacingUI.Instance.claimRaceBackgroundFirstPlaceButtonText.text = "Claim successful";

            RacingUI.Instance.raceBackgroundFirstPlacePanel.SetActive(false);
        }

